import React, { Component } from 'react';
import PubNubReact from 'pubnub-react';

class Chat extends Component {
  constructor(props) {
    super(props);
    this.pubnub = new PubNubReact({ publishKey: 'pub-c-bf4fe468-70b4-46de-9837-0ef63cd6a138', subscribeKey: 'sub-c-1f66d32e-68b1-11e9-8122-22455f4026bf' });
    this.pubnub.init(this);
  }

  componentWillMount() {
    this.pubnub.subscribe({ channels: ['channel1'], withPresence: true });

    this.pubnub.getMessage('channel1', (msg) => {
      console.log(msg);
    });

    this.pubnub.getStatus((st) => {
      console.log(st);
      this.pubnub.publish({ text: input.value, channel: 'channel1' });
    });
  }

  componentWillUnmount() {
    this.pubnub.unsubscribe({ channels: ['channel1'] });
  }

  render() {
    return (
    <div>
        <h3>Chat With Others Here</h3>

        <section id="main" role="main">
            <div id="chat">
                <input type="text"
                placeholder="Type your message..."
                value = {this.state.message}
                onchange = {e => this.handleChnage(e)}
                ></input>
            </div>
            <button id="button">
                Send Message
            </button>

            <p id="presence"></p>
            <div id="output"></div>
        </section>
    </div>
    )
  }
}
export default Chat;
