import React, { Component } from 'react';
import ReactDOM from 'react-dom';
// import './index.css';

import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Input from '@material-ui/core/Input';

import PubNubReact from 'pubnub-react';


const now = new Date().getTime();
const username = ['user', now].join('-');

class Message extends Component{

  render () {
      return ( 
        <div > { this.props.uuid }: { this.props.text } 
        </div>
      );
  }
};

class ChatArea extends Component {

  constructor(props) {
    super(props);
    this.pubnub = new PubNubReact({
        publishKey: 'pub-c-af9e408a-d4a8-473c-b591-81402cdf9aaf',
        subscribeKey: 'sub-c-7e76d5bc-2658-11e9-9508-c2e2c4d7488a',
        uuid: username
    });

    this.state = {
      messages: [],
      chatInput: '' 
    };
    this.pubnub.init(this);
  }

  sendChat = () => {
    if (this.state.chatInput) {
        this.pubnub.publish({
            message: {
              text: this.state.chatInput,
              uuid: username
            },
            channel: 'chatting'
        });
        this.setState({ chatInput: '' })
    }

  }

  setChatInput = (event) => {
    this.setState({ chatInput: event.target.value })
  }

  componentDidMount() {
    this.pubnub.subscribe({
        channels: ['chatting'],
        withPresence: true
    });

    this.pubnub.getMessage('chatting', (msg) => {
          this.pubnub.hereNow(
            {
                channels: ["chatting"],
                includeUUIDs: true,
                includeState: true
            },
            (status, response) => {
                console.log(status);
                console.log(response);
            }
        );
        const {text, uuid} = msg.message
        let messages = this.state.messages;
        messages.push(
          <Message key={ this.state.messages.length } uuid={ uuid } text={ text }/>
        );
        this.setState({
            messages: messages
        });
    });
  }

  handleKeyPress = (e) => {
    if (e.key === 'Enter') {
        this.sendChat();
    }
  }
  componentWillUnmount() {
    this.pubnub.unsubscribe({
        channels: ['chatting']
    });
  }

  render(){
    console.log(this.state.messages)
    const { classes } = this.props;
    return(
      <Card>
          <CardContent>
            <Typography gutterBottom variant="headline" component="h2">
              Messages
            </Typography>
              <div>
                <List component="nav">
                  <ListItem>
                  <Typography component="div">
                    { this.state.messages.props.text }
                  </Typography>
                  </ListItem>
                </List>
              </div>
          </CardContent>
          <CardActions>
            <Input
              placeholder="Enter a message"
              value={this.state.chatInput}
              onKeyDown={this.handleKeyPress}
              onChange={this.setChatInput}
              inputProps={{
                'aria-label': 'Description',
              }}
            />
          </CardActions>
        </Card>
      );
    }
}
export default ChatArea;
