import React, { Component } from 'react';
import PubNubReact from 'pubnub-react';

export default class extends Component {
    constructor(props) {
        super(props);
        this.pubnub = new PubNubReact({
            subscribe_key: 'sub-c-1f66d32e-68b1-11e9-8122-22455f4026bf',
            publish_key: 'pub-c-bf4fe468-70b4-46de-9837-0ef63cd6a138'
        });
        this.pubnub.init(this);
    }

    componentWillMount() {
        this.pubnub.subscribe({
            channels: ['channel1'],
            withPresence: true
        });

        this.pubnub.getMessage('channel1', (msg) => {
            console.log(msg);
        });

        this.pubnub.getStatus((st) => {
            this.pubnub.publish({
                message: 'hello world from react',
                channel: 'channel1'
            });
        });
    }

    componentWillUnmount() {
        this.pubnub.unsubscribe({
            channels: ['channel1']
        });
    }

    render() {
        const messages = this.pubnub.getMessage('channel1');
        return (
            <div>

               <section id="main" role="main">
                    <div id="chat">
                        <i id="avatar" class="face12"><input type="text" id="input" placeholder="Type your message..."></input></i>
                    </div>
                    <button id="button">
                        Send Message
                    </button>

                    <p id="presence"></p>
                    <div id="output"></div>
                </section>
            </div>
        );
    }
}
