/**
 * Generate new id
 * @function newId
 * @param {Object} [options={}] - Optional settings
 * @param {string} [options.prefix-'the'] - Id prefix
 * @returns {string}
 */
'use strict'

import uuid from 'uuid'

/** @lends newId */
function newId(options = {}) {
  let { prefix = 'the' } = options
  return [prefix, uuid.v4()].join('-')
}

export default newId
